import { useState } from 'react';

interface FavoriteButtonProps {
  productId: number;
  productName: string;
}

export default function FavoriteButton({ productId, productName }: FavoriteButtonProps) {
  const [isFavorite, setIsFavorite] = useState(false);

  const toggleFavorite = () => {
    setIsFavorite(!isFavorite);
    console.log(`${isFavorite ? 'Removed' : 'Added'} "${productName}" ${isFavorite ? 'from' : 'to'} favorites`);
  };

  return (
    <button
      onClick={toggleFavorite}
      style={{
        padding: '0.5rem 1rem',
        backgroundColor: isFavorite ? '#ef4444' : '#3b82f6',
        color: 'white',
        border: 'none',
        borderRadius: '0.375rem',
        cursor: 'pointer',
        fontWeight: '500',
        transition: 'background-color 0.2s',
      }}
      onMouseEnter={(e) => {
        e.currentTarget.style.backgroundColor = isFavorite ? '#dc2626' : '#2563eb';
      }}
      onMouseLeave={(e) => {
        e.currentTarget.style.backgroundColor = isFavorite ? '#ef4444' : '#3b82f6';
      }}
    >
      {isFavorite ? 'Remove from Favorites' : 'Add to Favorites'}
    </button>
  );
}

