import { useState } from 'react';

interface FavoriteButtonProps {
  productId: string;
  productName: string;
}

export default function FavoriteButton({ productId, productName }: FavoriteButtonProps) {
  const [isFavorite, setIsFavorite] = useState(false);

  const handleToggle = () => {
    setIsFavorite(!isFavorite);
    console.log(`${isFavorite ? 'Removed from' : 'Added to'} favorites: ${productName}`);
  };

  return (
    <button
      onClick={handleToggle}
      style={{
        padding: '8px 16px',
        backgroundColor: isFavorite ? '#ff6b6b' : '#4CAF50',
        color: 'white',
        border: 'none',
        borderRadius: '4px',
        cursor: 'pointer',
        fontSize: '14px',
        transition: 'background-color 0.3s'
      }}
      aria-label={isFavorite ? `Remove ${productName} from favorites` : `Add ${productName} to favorites`}
    >
      {isFavorite ? '‚ù§Ô∏è Remove from Favorites' : 'ü§ç Add to Favorites'}
    </button>
  );
}

